g style="color: red"><?php echo ($finshtotal); ?></strong><br/> 											总金额：<strong style="color: green"><?php echo ($alltotal+$alltotalfinsh); ?></strong>　总已出账金额：<strong style="color: red"><?php echo ($alltotalfinsh); ?></strong><br/><?php endif; ?>										<!--本页总金额：<strong style="color: green"><?php echo ($total); ?></strong> 本页已出账金额：<strong style="color: red"><?php echo ($finshtotal); ?></strong><br/> 总金额：<strong style="color: green"><?php echo ($alltotal+$alltotalfinsh); ?></strong> 总已出账金额：<strong style="color: red"><?php echo ($alltotalfinsh); ?></strong-->										</td>									</tr>									<tr class="odd">										<td colspan="14" id="show_count"></td>									</tr>									<tr><td class="textcenter pagebar" colspan="14"><?php echo ($pagebar); ?></td></tr>									<?php else: ?>									<tr class="odd"><td class="textcenter red" colspan="14" >该的店铺暂时还没有订单。</td></tr><?php endif; ?>						</tbody>					</table>				</div>			</form>		</div><script type="text/javascript">
$(document).ready(function(){	$('#all_select').click(function(){		if ($(this).attr('checked')){			$('.select').attr('checked', true);		} else {			$('.select').attr('checked', false);		}		total_price();	});	$('.select').click(function(){total_price();});	$('.button').click(function(){		var strids = '';		var pre = ''		$('.select').each(function(){			if ($(this).attr('checked')) {				strids += pre + $(this).val();				pre = ',';			}		});		if (strids.length > 0) {			$.get("<?php echo U('Merchant/change',array('mer_id'=>$mer_id));?>", {strids:strids}, function(data){				if (data.error_code == 0) {					location.reload();				}			}, 'json');		}	});});function total_price(){	var total = 0;	$('.select').each(function(){		if ($(this).attr('checked')) {			total += parseFloat($(this).attr('data-price'));		}	});	total = Math.round(total * 100)/100;	var percent = $('#percent').val();	if (total > 0) {		$('#show_count').html('账单总计金额：<strong style=\'color:red\'>￥' + total + '</strong>, 平台对改商家的抽成比例是：<strong style=\'color:green\'>' + percent + '%</strong>, 平台应得金额：<strong style=\'color:green\'>￥' + Math.round(total * percent) /100 + '</strong>,商家应得金额:<strong style=\'color:red\'>￥' + Math.round((total - Math.round(total * percent) /100) * 100)/100 + '</strong>');	} else {		$('#show_count').html('');	}}
</script>	</body>
